export function disconnectEdge(nodeState, edge, direction) {
    // Disconnect where the edge comes from
    if (edge[direction]) {
        const port = nodeState[edge[direction].nodeId].ports.find((p) => p.id === edge[direction].portId);
        if (port) {
            // Remove reference to edge in start port
            if (port.edgeIds) {
                port.edgeIds = port.edgeIds.filter((edgeId) => edgeId !== edge.id);
            }
        }
    }
}
