import React from "react";
import { NodeContext } from "@oxenode/core";
export default function Image({ name = "", nodeId, width = "200px", value = "" }) {
    const { nodeState, dispatchNode } = React.useContext(NodeContext);
    const node = nodeState[nodeId];
    if (typeof node.State[name] === "string")
        value = node.State[name];
    React.useEffect(() => {
        dispatchNode({
            type: "UPDATE_NODE_STATE",
            payload: { node, name, value },
        });
    }, [value]);
    const url = (nodeState[nodeId].State || {})[name] || "";
    return (React.createElement("img", { style: {
            'pointerEvents': 'none'
        }, src: url || './logo.svg', width: width }));
}
